import React, { Component } from "react";
import { v4 as uuidv4 } from "uuid";

class GuestList extends Component {
  state = {
    contacts: [
      { id: "id-1", name: "Rosie Simpson", number: "459-12-56" },
      { id: "id-2", name: "Hermione Kline", number: "443-89-12" },
      { id: "id-3", name: "Eden Clements", number: "645-17-79" },
      { id: "id-4", name: "Annie Copeland", number: "227-91-26" },
    ],
  };

  onDeleteGuest = (index) => {
    // const contacts = this.state.contacts;
    const { contacts } = this.state;
    contacts.splice(index, 1);
    this.setState({ contacts });
  };
  //   onDeleteGuest = (id) => {
  //     this.setState((prev) => ({
  //       contacts: prev.contacts.filter((contact) => contact.id !== id),
  //     }));
  //   };

  clearInputs = () => {
    this.setState({ ...this.formInitialState });
  };

  onAddPerson = (personData) => {
    this.setState((prev) => ({
      contacts: [...prev.contacts, personData],
    }));
    this.setState({ ...this.formInitialState });
  };

  render() {
    return (
      <div className="GuestList">
        <AddGuest
          onAddPerson={this.onAddPerson}
          clearInputs={this.clearInputs}
        />
        <br />
        <div className="Guest">
          <table>
            <tbody>
              <tr>
                <th>Name</th>
                <th>Number</th>
              </tr>
              {this.state.contacts.map((user, index) => {
                return (
                  <Guest
                    user={user}
                    key={uuidv4()}
                    index={index}
                    onDeleteGuest={this.onDeleteGuest}
                  />
                );
              })}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}

const Guest = (props) => {
  return (
    <tr>
      <td>{props.user.name}</td>
      <td>{props.user.number}</td>
      <td>
        <button
          className="delete"
          onClick={() => {
            props.onDeleteGuest(props.index);
          }}
        >
          delete
        </button>
      </td>
    </tr>
  );
};

class AddGuest extends Component {
  state = {
    name: "",
    number: "",
  };

  cancelCourse = () => {
    this.setState({
      name: "",
      number: "",
    });
  };

  inputHandler = ({ target }, cancelCourse) => {
    const { value, name } = target;
    this.setState({
      [name]: value,
    });
    cancelCourse();
    // this.setState({ ...this.initialState });
  };

  render() {
    return (
      <div>
        Name :&nbsp;
        <input
          type="text"
          name="name"
          value={this.state.name}
          onChange={this.inputHandler}
        />
        <br />
        <br />
        Number :&nbsp;
        <input
          type="text"
          name="number"
          value={this.state.number}
          onChange={this.inputHandler}
        />
        <br />
        <br />
        <button
          className="addGuest"
          onClick={() => this.props.onAddPerson(this.state)}
        >
          Add Guest
        </button>
      </div>
    );
  }
}

export default GuestList;
