import React from "react";
import { connect } from "react-redux";

const Guest = (props) => {
  return (
    <div className="contact">
      <h2 className="contactName">{props.user.name}</h2>
      <p>{props.user.number}</p>
      <button
        id={props.user.id}
        className="delete"
        onClick={props.onDeleteGuest}
      >
        Delete
      </button>
    </div>
  );
};

const mapStateToProps = (state) => {
  const { items, filter } = state;
  const normalizedFilter = filter.toLowerCase();
  const filteredContacts = items.filter(
    (person) => person.name.toLowerCase().indexOf(normalizedFilter) !== -1
  );

  return {
    contacts: filteredContacts,
  };
};

export default connect(mapStateToProps)(Guest);
