const onAdd = createAction(actionTypes.ADD, (personData) => ({
  payload: { ...personData, id: uuid() },
}));
const onSearch = createAction(actionTypes.SEARCH, (e) => ({
  payload: e.target.value,
}));

const onDelete = createAction(actionTypes.DELETE, (e) => ({
  payload: e.target.id,
}));

export default {
  onDelete,
  onAdd,
  onSearch,
};
