import React, { Component } from "react";
import { v4 as uuidv4 } from "uuid";

class GuestList extends Component {
  state = {
    contacts: [
      { id: "id-1", name: "Rosie Simpson", number: "459-12-56" },
      { id: "id-2", name: "Hermione Kline", number: "443-89-12" },
      { id: "id-3", name: "Eden Clements", number: "645-17-79" },
      { id: "id-4", name: "Annie Copeland", number: "227-91-26" },
    ],
    filter: "",
    name: "",
    number: "",
  };

  onDeleteGuest = (index) => {
    const { contacts } = this.state;
    contacts.splice(index, 1);
    this.setState({ contacts });
  };

  onAddPerson = (personData) => {
    this.setState((prev) => ({
      contacts: [...prev.contacts, personData],
    }));
  };

  updateSearch(event) {
    this.setState({ filter: event.target.value.substr(0, 20) });
  }

  render() {
    let filteredContacts = this.state.contacts.filter((contact) => {
      return (
        contact.name.toLowerCase().indexOf(this.state.filter.toLowerCase()) !==
        -1
      );
    });
    return (
      <div className="GuestList">
        <AddGuest onAddPerson={this.onAddPerson} />
        <br />
        <div className="Guest">
          <table>
            <tbody>
              {this.state.contacts.length >= 1 ? (
                <tr>
                  <th>Name</th>
                  <th>Number</th>
                </tr>
              ) : (
                <tr></tr>
              )}
              {this.state.contacts.map((user, index) => {
                return (
                  <Guest
                    user={user}
                    key={uuidv4()}
                    index={index}
                    onDeleteGuest={this.onDeleteGuest}
                  />
                );
              })}
            </tbody>
          </table>
          <h2>Find contact by name</h2>
          <input
            type="text"
            value={this.state.filter}
            onChange={this.inputHandler}
          />
        </div>
      </div>
    );
  }
}

const Guest = (props) => {
  return (
    <tr>
      <td>{props.user.name}</td>
      <td>{props.user.number}</td>
      <td>
        <button
          className="delete"
          onClick={() => {
            props.onDeleteGuest(props.index);
          }}
        >
          delete
        </button>
      </td>
    </tr>
  );
};

class AddGuest extends Component {
  state = {
    name: "",
    number: "",
  };

  inputHandler = ({ target }) => {
    const { value, name } = target;
    this.setState({
      [name]: value,
    });
  };

  postNum = () => {
    this.props.onAddPerson(this.state);
    this.setState({ name: "", number: "" });
  };

  render() {
    return (
      <div>
        Name :&nbsp;
        <input
          type="text"
          name="name"
          value={this.state.name}
          onChange={this.inputHandler}
        />
        <br />
        <br />
        Number :&nbsp;
        <input
          type="text"
          name="number"
          value={this.state.number}
          onChange={this.inputHandler}
        />
        <br />
        <br />
        <button className="addGuest" onClick={this.postNum}>
          Add Guest
        </button>
      </div>
    );
  }
}

export default GuestList;
