import React, { Component } from "react";

const Guest = (props) => {
  return (
    <tr>
      <td>{props.user.name}</td>
      <td>{props.user.number}</td>
      <td>
        <button
          className="delete"
          onClick={() => {
            this.onDeleteGuest(props.index);
          }}
        >
          delete
        </button>
      </td>
    </tr>
  );
};

class GuestList extends Component {
  state = {
    guestUsersList: [
      {
        name: "Alex",
        number: 678900123,
      },
    ],
  };
  render() {
    return (
      <div className="GuestList">
        <div className="Guest">
          <table>
            <tbody>
              <tr>
                <th>Name</th>
                <th>Number</th>
              </tr>
              {this.state.guestUsersList.map((user, index) => {
                return <Guest user={user} key={Date.now()} index={index} />;
              })}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}

export default GuestList;
