import React from "react";
import { CSSTransition, TransitionGroup } from "react-transition-group";
import Guest from "../Guest";
import AddGuest from "../AddGuest";
import actions from "../../actions/actions";
import { connect } from "react-redux";
import contactOperations from "../../operations/contactOperations";

import "../Notifications/AlertAnimation.css";

// components/GuestList
const GuestList = ({ onAdd, search, onSearch, data }) => (
  <div className="phoneBookList">
    <AddGuest data={data} onAdd={onAdd} />
    <div className="Guest">
      <input
        type="text"
        value={search}
        onChange={onSearch}
        className="filter"
        placeholder="Find contact by name"
      />
      <TransitionGroup className="Guests">
        {data.map((user) => (
          <CSSTransition
            key={user.id}
            timeout={250}
            classNames="Guests-item-fade"
            unmountOnExit
          >
            <Guest id={user.id} />
          </CSSTransition>
        ))}
      </TransitionGroup>
    </div>
  </div>
);

const mapStateToProps = (state) => {
  const { filter: search } = state.contacts;
  return {
    data: state.contacts.items.filter(
      (person) => person.name.toLowerCase().indexOf(search.toLowerCase()) !== -1
    ),
    search: state.contacts.filter,
  };
};

const mapDispatchToProps = {
  onAdd: contactOperations.addContact,
  onSearch: actions.onSearch,
};

export default connect(mapStateToProps, mapDispatchToProps)(GuestList);

