import React, { Component } from "react";

class GuestList extends Component {
  state = {
    guestUsersList: [
      {
        name: "Alex",
        number: 678900123,
      },
    ],
  };

  onDeleteGuest = (index) => {
    console.log(index);
  };

  render() {
    return (
      <div className="GuestList">
        <div className="Guest">
          <table>
            <tbody>
              <tr>
                <th>Name</th>
                <th>Number</th>
              </tr>
              {this.state.guestUsersList.map((user, index) => {
                return (
                  <Guest
                    user={user}
                    key={Date.now()}
                    index={index}
                    onDeleteGuest={this.onDeleteGuest}
                  />
                );
              })}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}

const Guest = (props) => {
  console.log(prop);
  return (
    <tr>
      <td>{props.user.name}</td>
      <td>{props.user.number}</td>
      <td>
        <button
          className="delete"
          onClick={() => {
            props.onDeleteGuest(props.index);
          }}
        >
          delete
        </button>
      </td>
    </tr>
  );
};

export default GuestList;
