import contactsActions from "../actions/actions";
import axios from "axios";

const addContact = (contact) => (dispatch) => {
  dispatch(contactsActions.onAddRequest());
  axios
    .post("http://localhost:2000/contacts", { ...contact })
    .then((response) => dispatch(contactsActions.onAddSuccess(response.data)))
    .catch((error) => dispatch(contactsActions.onAddFail(error)));
};

const getContacts = () => (dispatch) => {
  dispatch(contactsActions.onAddFetch());
  axios
    .get("http://localhost:2000/contacts")
    .then((response) =>
      dispatch(contactsActions.onAddFetchSuccess(response.data))
    )
    .catch((error) => dispatch(contactsActions.onAddFetchError(error)));
};

// const onAddFetch = createAction(actionTypes.FETCH);
// const onAddFetchSuccess = createAction(actionTypes.FCS);
// const onAddFetchError = createAction(actionTypes.FCF);

export default {
  addContact,
  getContacts,
};
